sources.assets/css/sass/layout/_navigation.scss:
.navigation {
	position: relative;
	display: flex;
	align-items: center;
	background-color: var(--primary);

	position: fixed;
	width: 100%;
	top: 0;
	z-index: 100;
}

.wrapper {
	width: 85%;
	max-width: 120rem;
	margin: 0 auto;
	display: flex;
	align-items: center;
	justify-content: space-between;

	.logo {
		padding: 2rem 0;
		margin-right: 3.5rem;
	}

	.nav-toggle {
		background: none;
		border: none;
		padding: 0;
		display: flex;
		align-items: center;
		cursor: pointer;
		@media only screen and(min-width:$v-7) {
			display: none;
		}

		svg {
			stroke: var(--white);
			width: 3.5rem;
			height: 3.5rem;
		}
	}

	//JS triggers checked
	nav.checked {
		transform: scale(1, 1);
	}
	nav.checked li {
		opacity: 1;
		transition: opacity 200ms ease-in-out 400ms;
	}

	nav {
		position: absolute;
		left: 0;
		top: 6rem;
		width: 100%;
		z-index: 10;

		background-color: var(--primary);

		transform: scale(1, 0);
		transform-origin: top;
		transition: transform 400ms ease-in-out;

		ul {
			padding: 1rem 0;
		}
		li {
			display: flex;
			position: relative;
			width: 100%;

			opacity: 0;
			transition: opacity 100ms;
			transition: background-color 250ms ease-in-out;
			a {
				width: 100%;
				height: 100%;
				display: block;
				padding: 2rem 0;
				padding-left: 7.5%;
				cursor: pointer;
				transition: background-color 250ms ease-in-out;
			}
		}

		@media only screen and(min-width:$v-7) {
			position: relative;
			top: 0;
			background-color: transparent;
			transform: scale(1, 1);
			width: auto;
			ul {
				display: flex;

				li {
					opacity: 1;
					width: auto;

					a {
						padding: 1rem 2rem;
					}

					a:hover {
						opacity: 0.9;
					}
				}

				li:last-child {
					a {
						padding-right: 0;
					}
				}
			}
		}
	}
}

