.intro {
	background-color: var(--white);
	color: var(--font-header);

	display: flex;
	flex-direction: column;

	padding: 10rem 0;
	width: 85%;
	max-width: 120rem;
	margin: 0 auto;

	@media only screen and(min-width:$v-9) {
		flex-direction: row;
	}
}

.intro-left {
	display: flex;
	flex-direction: column;
	align-items: center;

	.intro-title,
	.intro-subtitle,
	.intro-paragraph {
		width: 100%;
		max-width: 50rem;
	}

	.section-title {
		color: var(--accent);
	}
	.intro-subtitle {
		font-weight: 800;
	}

	@media only screen and(min-width:$v-9) {
		padding-right: 1.5rem;
		flex: 1;
		align-items: flex-start;

		.intro-title,
		.intro-subtitle,
		.intro-paragraph {
			max-width: 40rem;
		}
	}
}
.intro-right {
	margin-top: 10rem;

	@media only screen and(min-width:$v-9) {
		flex: 1.3;
		margin-top: 7rem;
		padding-left: 1.5rem;
		.intro-image {
			max-width: 65rem;
		}
	}

	.intro-image {
		width: 100%;

		border-radius: 3px;
		overflow: hidden;
	}

	.intro-image-container {
		z-index: 10;
		position: relative;
	}
	.intro-image-container::after {
		content: '';
		width: 100%;

		height: 100%;
		position: absolute;
		top: -3.5rem;
		left: 3.5rem;
		background-color: var(--primary);
		border-radius: 3px;
		z-index: -1;
	}
}

